content.css:
.df-footer-columns {
  background: var(--df-background);
  padding: 4rem 0 0;
  color: var(--df-text);
}

.df-footer-grid {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 2rem;
}

.df-footer-logo img {
  max-height: 40px;
  margin-bottom: 1rem;
}

.df-footer-description {
  margin-bottom: 1.5rem;
  line-height: 1.6;
}

.df-social-links {
  display: flex;
  gap: 1rem;
}

.df-social-links a {
  color: var(--df-text);
  font-size: 1.25rem;
  transition: color 0.3s ease;
}

.df-social-links a:hover {
  color: var(--df-primary);
}

.df-footer-column h3 {
  font-size: 1.25rem;
  margin-bottom: 1.5rem;
  color: var(--df-text);
}

.df-footer-column ul {
  list-style: none;
  padding: 0;
  margin: 0;
}

.df-footer-column ul li {
  margin-bottom: 0.75rem;
}

.df-footer-column ul a {
  color: var(--df-text);
  text-decoration: none;
  transition: color 0.3s ease;
}

.df-footer-column ul a:hover {
  color: var(--df-primary);
}

.df-contact-info li {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-bottom: 1rem;
}

.df-footer-bottom {
  margin-top: 4rem;
  padding: 1.5rem 0;
  border-top: 1px solid var(--df-border);
}

.df-copyright {
  text-align: center;
  margin: 0;
  color: var(--df-text);
}

@media (max-width: 992px) {
  .df-footer-grid {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media (max-width: 576px) {
  .df-footer-grid {
    grid-template-columns: 1fr;
  }
}
